import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import Ptt from './Ptt';

export default function App() {

  const [socket, setSocket] = useState(null);
  const [channel, setChannel] = useState(localStorage.getItem('channel'));

  useEffect(() => {
    const newSocket = io('http://localhost:9000');
    setSocket(newSocket);
    return () => {
      newSocket.disconnect();
      newSocket.close();
      newSocket.removeAllListeners();
    };
  }, [setSocket]);

  useEffect(() => {
    if (localStorage.getItem('channel') === null) {
      localStorage.setItem('channel', 3000);
    }

    setChannel(localStorage.getItem('channel'));
  }, []);

  socket.on('connect', () => {
    socket.emit('join-room', channel);
    // setState('Idle');
    // setId(socket.id);
    console.log(`You are connected with socketID: ${socket.id} in Channel: ${channel}`);
  });

  socket.on('connect_error', error => {
    // setState('Connection-lost');
    // setIsWarning(true);
    // setWarningMessage('Connection lost!');
    console.log("Socket Error: " + error);
    socket.disconnect();
    socket.close();
    socket.removeAllListeners();

    setTimeout(() => {
      // setState('Reconnecting');
      // setWarningMessage('Reconnecting..');
      socket.connect();
    }, 2000);
  });

  return(
    <div className="App">
      { socket ? (
        <Ptt
          socket={socket}
          channel={channel}
          setChannel={setChannel}
        />
      ) : (
        <div>Not Connected</div>
      )}
    </div>
  );
}
